import React, {useState,useRef} from "react";
import {Link} from "react-router-dom";
import Input from "react-validation/build/input";
import Form from "react-validation/build/form";

const required = (value) => {
  if (!value) {
    return (
      <div className="alert alert-danger" role="alert">
        This field is required!
      </div>
    );
  }
};

const validatePNR = (value) => {
  if (!/^[0-9]{13}$/.test(value)) {
    return (
      <div className="alert alert-danger" role="alert">
        PNR must be a 13-digit number!
      </div>
    );
  }
};

const PnrSearch = () => {

const [inputMessage,setInputMessage]=useState("");
const [inputName,setInputName]=useState("");
const [loading, setLoading] = useState(false);
const [nloading, setNloading] = useState(false);

const inputRef = useRef();
const formRef =useRef();


const handleChangeInput = (event)=>{
  setInputMessage(event.target.value)
}
const handleChangeName = (event)=>{
  setInputName(event.target.value)
}

const handleClick = (event) => {
  event.preventDefault();

  inputRef.current.validateAll();
  setLoading(false);

  if (inputMessage.trim().length === 0) {
    setLoading(false);
  }else{
  setLoading(true);
  }
};
const handleName = (event)=>{
  event.preventDefault();
  form.current.validateAll();
  setNloading(false);
  if(inputName.trim().length === 0){
    setNloading(false);
  }else{
    setNloading(true);
  }
}



  return (
    <div>
      <h2 className="ui header" style={{ color: "pink" }}>
        PNR SEARCH
      </h2>
      <Form ref={inputRef}>
      <div className="ui fluid action input">
        <Input type="number"
        pattern="[0-9]{13}"
         placeholder="PNR Number"
         value ={inputMessage}
         onChange={(e) => setInputMessage(e.target.value)}
            validations={[required, validatePNR]}
          />
          </div>
          <button className="ui toggle button" onClick={handleClick}>
          {loading ? (
            <Link
              to={{
                pathname: "/train-pnr",
                state: { inputMessage },
              }}
            >
              {<div className="active item">Search</div>}
            </Link>
          ) : (
            <div className="active item">Search</div>
          )}
        </button>
      </Form>



    </div>
  );
};

export default PnrSearch;
